mapper.insert:
INSERT INTO ticket (ticket_no, festival_no, party_no, ticket_price, ticket_count)
VALUES (
	seq_ticket_ticket_no.nextval,
	
	#{festival.festivalNo}
	
	
	null
	,
	
	 #{party.partyNo}
	
	
	null
	,
	#{ticketPrice},
	#{ticketCount}
)

mapper.update:
UPDATE ticket

	
		ticket_price = #{ticketPrice},
	
	
		ticket_count = #{ticketCount}
	


	
		festival_no = #{festival.festivalNo}
	
	
		party_no = #{party.partyNo}

UPDATE ticket
SET	ticket_count = #{ticketCount}
WHERE ticket_no = #{ticketNo}
